package loops;

public class CountingForSeveralTimes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int j = 1; j <= 20; j++) {
		
			for (int i = 1; i <= 10; i++) {
			
			System.out.print(i + " ");
			}
			// For new line.
			System.out.println();
		}
	}

}
