package loops;

public class ForLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i<=100; i = i + 1) {
			
			System.out.println("Hello World");
		}
		
		for (int i = 0; i<=100; i = i + 2) {
			
			System.out.println(i);
		}
	}

}
